import React, { useEffect, useState } from "react";
import Book from "./components/Book";
import "./App.css";

function App() {
  const [books, setBooks] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({
    title: "",
    author: "",
    url: "",
  });
  const [editingBookId, setEditingBookId] = useState(null);
  const [filter, setFilter] = useState("");

  // Load from localStorage or books.json
  useEffect(() => {
    const saved = localStorage.getItem("books");
    if (saved) {
      setBooks(JSON.parse(saved));
    } else {
      fetch("/data/books.json")
        .then((res) => res.json())
        .then((json) =>
          setBooks(
            json.map((b) => ({
              ...b,
              selected: false,
            }))
          )
        );
    }
  }, []);

  // Save books to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem("books", JSON.stringify(books));
  }, [books]);

  // Select book (only one at a time)
  const handleSelect = (id) => {
    setBooks((prevBooks) =>
      prevBooks.map((b) => ({
        ...b,
        selected: b.isbn13 === id ? !b.selected : false,
      }))
    );
  };

  // Delete selected book
  const handleDelete = () => {
    setBooks((prevBooks) => prevBooks.filter((b) => !b.selected));
  };

  // Add new book (keep same structure)
  const handleAdd = (e) => {
    e.preventDefault();

    const newBook = {
      isbn13: Date.now().toString(),
      title: formData.title,
      author: formData.author,
      url: formData.url,
      image: formData.url || "https://via.placeholder.com/150x200",
      selected: false,
    };

    setBooks((prev) => [...prev, newBook]);
    setFormData({ title: "", author: "", url: "" });
    setShowModal(false);
  };

  // Edit selected book â€” same form structure as Add
  const handleEdit = () => {
    const selectedBook = books.find((b) => b.selected);
    if (!selectedBook) {
      alert("Please select a book to edit.");
      return;
    }

    // Fill the form with existing book data
    setFormData({
      title: selectedBook.title || "",
      author: selectedBook.author || "",
      url: selectedBook.url || "",
    });
    setEditingBookId(selectedBook.isbn13);
    setShowModal(true);
  };

  // Submit edit
  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingBookId) {
      setBooks((prevBooks) =>
        prevBooks.map((b) =>
          b.isbn13 === editingBookId
            ? {
                ...b,
                title: formData.title,
                author: formData.author,
                url: formData.url,
                image: formData.url || "https://via.placeholder.com/150x200",
              }
            : b
        )
      );
      setEditingBookId(null);
      setFormData({ title: "", author: "", url: "" });
      setShowModal(false);
    } else {
      handleAdd(e);
    }
  };

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Filter by author (safe)
  const filteredBooks = books.filter((book) =>
    (book.author?.toLowerCase() || "").includes(filter.toLowerCase())
  );

  return (
    <div className="app">
      <header className="header">
        <img src="logo.svg" alt="Brand Logo" className="logo" />
        <h1>Elf Book Catalog</h1>
      </header>

      <main className="content">
        <div className="button-column">
          <button className="add-btn" onClick={() => setShowModal(true)}>
            + Add
          </button>
          <button className="update-btn" onClick={handleEdit}>
            âœŽ Edit
          </button>
          <button className="delete-btn" onClick={handleDelete}>
            ðŸ—‘ Delete
          </button>

          <div className="filter-section">
            <label>Filter by Author:</label>
            <input
              type="text"
              value={filter}
              placeholder="Type author name..."
              onChange={(e) => setFilter(e.target.value)}
            />
          </div>
        </div>

        <div className="grid">
          {filteredBooks.map((book) => (
            <Book
              key={book.isbn13}
              id={book.isbn13}
              title={book.title}
              author={book.author}
              price={book.price}
              image={book.image}
              url={book.url}
              selected={book.selected}
              onSelect={handleSelect}
            />
          ))}
        </div>
      </main>

      <footer className="footer">
        <p>Â© 2025 Elf Book Catalog. All rights reserved.</p>
      </footer>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingBookId ? "Edit Book" : "Add a New Book"}</h2>
            <form onSubmit={handleSubmit}>
              <label>
                Title:
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  required
                />
              </label>
              <label>
                Author:
                <input
                  type="text"
                  name="author"
                  value={formData.author}
                  onChange={handleChange}
                  required
                />
              </label>
              <label>
                Cover URL:
                <input
                  type="text"
                  name="url"
                  value={formData.url}
                  onChange={handleChange}
                />
              </label>

              <div className="modal-actions">
                <button type="submit">
                  {editingBookId ? "Update" : "Add"}
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setShowModal(false);
                    setEditingBookId(null);
                    setFormData({ title: "", author: "", url: "" });
                  }}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
